#Questão: Índices de caracteres



sequencia = input()
busca = input()

for letra in range(len(busca)):
    cont = ""

    for i in range(len(sequencia)):


        if i == 0 and len(busca) == 1:
            if busca[letra] == sequencia[i]:
                cont = cont + str(i)

        elif i == 0:
            if busca[letra] == sequencia[i]:
                cont = cont + str(i) + " "

        elif i == (len(sequencia)-1):
            if busca[letra] == sequencia[i]:
                cont = cont + str(i)
        else:
            if busca[letra] == sequencia[i]:
                cont = cont + str(i)

    if cont == "":
        print("-1")
    else:
        print(cont)
